<?php// контроллер - ядро :abstract class Core{    // конструктор класса :    function __construct()    {    }    // полная обработка HTTP запроса :    public function Request()    {        $this->OnInput();        $this->OnOutput();    }    // виртуальный обработчик запроса :    protected function OnInput()    {    }    // виртуальный генератор HTML :    protected function OnOutput()    {    }    // запрос произведен методом GET?    protected function IsGet()    {        return $_SERVER['REQUEST_METHOD'] == 'GET';    }    // запрос произведен методом POST?    protected function IsPost()    {        return $_SERVER['REQUEST_METHOD'] == 'POST';    }    // генерация HTML шаблона в строку :    protected function View($Object, $vars = array())    {        // установка переменных для шаблона :        foreach ($vars as $k => $v) {            $$k = $v;        }//        print_r($vars);//        die();        // генерация HTML в строку :        ob_start();        include $Object;        return ob_get_clean();    }}