<?phpinclude_once('Model/Model.php');include_once('Controller/C_Base.php');// конттроллер главной страници консоли редактора :class C_Home extends C_Base{		// конструктор :	function __construct()	{	}			// виртуальный обработчик запроса :		protected function OnInput()	{		parent::OnInput();		$this->title = $this->title . 'Home';	}		// виртуальный генератор HTML :	protected function OnOutput()	{		$this->content = $this->View('View/v_home.php');		parent::OnOutput();	}	}